SELECT 
	 <br>SUM(CASE WHEN  VE_TIPO='1'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_arquibancadas,  <br>SUM(CASE WHEN  VE_TIPO='1'  THEN  1  ELSE 0 END) AS qtde_arquibancadas,  <br>SUM(CASE WHEN  VE_TIPO='2'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_frisas,  <br>SUM(CASE WHEN  VE_TIPO='2'  THEN  1  ELSE 0 END) AS qtde_frisas,  <br>SUM(CASE WHEN  VE_TIPO='3'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_camarotes,  <br>SUM(CASE WHEN  VE_TIPO='3'  THEN  1  ELSE 0 END) AS qtde_camarotes,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_folia,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  THEN  1  ELSE 0 END) AS qtde_folia,  <br>SUM(CASE WHEN  VE_TIPO='6'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_super,  <br>SUM(CASE WHEN  VE_TIPO='6'  THEN  1  ELSE 0 END) AS qtde_super,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_lounges,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  THEN  1  ELSE 0 END) AS qtde_lounges, 
	 <br>SUM(CASE WHEN  VE_TIPO='1'  AND  VE_DIA='33'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_arquibancadas_33,  <br>SUM(CASE WHEN  VE_TIPO='1'  AND  VE_DIA='33'  THEN  1  ELSE 0 END) AS qtde_arquibancadas_33,  <br>SUM(CASE WHEN  VE_TIPO='1'  AND  VE_DIA='34'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_arquibancadas_34,  <br>SUM(CASE WHEN  VE_TIPO='1'  AND  VE_DIA='34'  THEN  1  ELSE 0 END) AS qtde_arquibancadas_34,  <br>SUM(CASE WHEN  VE_TIPO='1'  AND  VE_DIA='35'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_arquibancadas_35,  <br>SUM(CASE WHEN  VE_TIPO='1'  AND  VE_DIA='35'  THEN  1  ELSE 0 END) AS qtde_arquibancadas_35,  <br>SUM(CASE WHEN  VE_TIPO='1'  AND  VE_DIA='36'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_arquibancadas_36,  <br>SUM(CASE WHEN  VE_TIPO='1'  AND  VE_DIA='36'  THEN  1  ELSE 0 END) AS qtde_arquibancadas_36,  <br>SUM(CASE WHEN  VE_TIPO='1'  AND  VE_DIA='37'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_arquibancadas_37,  <br>SUM(CASE WHEN  VE_TIPO='1'  AND  VE_DIA='37'  THEN  1  ELSE 0 END) AS qtde_arquibancadas_37,  <br>SUM(CASE WHEN  VE_TIPO='2'  AND  VE_DIA='33'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_frisas_33,  <br>SUM(CASE WHEN  VE_TIPO='2'  AND  VE_DIA='33'  THEN  1  ELSE 0 END) AS qtde_frisas_33,  <br>SUM(CASE WHEN  VE_TIPO='2'  AND  VE_DIA='34'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_frisas_34,  <br>SUM(CASE WHEN  VE_TIPO='2'  AND  VE_DIA='34'  THEN  1  ELSE 0 END) AS qtde_frisas_34,  <br>SUM(CASE WHEN  VE_TIPO='2'  AND  VE_DIA='35'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_frisas_35,  <br>SUM(CASE WHEN  VE_TIPO='2'  AND  VE_DIA='35'  THEN  1  ELSE 0 END) AS qtde_frisas_35,  <br>SUM(CASE WHEN  VE_TIPO='2'  AND  VE_DIA='36'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_frisas_36,  <br>SUM(CASE WHEN  VE_TIPO='2'  AND  VE_DIA='36'  THEN  1  ELSE 0 END) AS qtde_frisas_36,  <br>SUM(CASE WHEN  VE_TIPO='2'  AND  VE_DIA='37'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_frisas_37,  <br>SUM(CASE WHEN  VE_TIPO='2'  AND  VE_DIA='37'  THEN  1  ELSE 0 END) AS qtde_frisas_37,  <br>SUM(CASE WHEN  VE_TIPO='3'  AND  VE_DIA='33'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_camarotes_33,  <br>SUM(CASE WHEN  VE_TIPO='3'  AND  VE_DIA='33'  THEN  1  ELSE 0 END) AS qtde_camarotes_33,  <br>SUM(CASE WHEN  VE_TIPO='3'  AND  VE_DIA='34'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_camarotes_34,  <br>SUM(CASE WHEN  VE_TIPO='3'  AND  VE_DIA='34'  THEN  1  ELSE 0 END) AS qtde_camarotes_34,  <br>SUM(CASE WHEN  VE_TIPO='3'  AND  VE_DIA='35'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_camarotes_35,  <br>SUM(CASE WHEN  VE_TIPO='3'  AND  VE_DIA='35'  THEN  1  ELSE 0 END) AS qtde_camarotes_35,  <br>SUM(CASE WHEN  VE_TIPO='3'  AND  VE_DIA='36'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_camarotes_36,  <br>SUM(CASE WHEN  VE_TIPO='3'  AND  VE_DIA='36'  THEN  1  ELSE 0 END) AS qtde_camarotes_36,  <br>SUM(CASE WHEN  VE_TIPO='3'  AND  VE_DIA='37'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_camarotes_37,  <br>SUM(CASE WHEN  VE_TIPO='3'  AND  VE_DIA='37'  THEN  1  ELSE 0 END) AS qtde_camarotes_37,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  AND  VE_DIA='33'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_folia_33,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  AND  VE_DIA='33'  THEN  1  ELSE 0 END) AS qtde_folia_33,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  AND  VE_DIA='34'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_folia_34,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  AND  VE_DIA='34'  THEN  1  ELSE 0 END) AS qtde_folia_34,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  AND  VE_DIA='35'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_folia_35,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  AND  VE_DIA='35'  THEN  1  ELSE 0 END) AS qtde_folia_35,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  AND  VE_DIA='36'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_folia_36,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  AND  VE_DIA='36'  THEN  1  ELSE 0 END) AS qtde_folia_36,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  AND  VE_DIA='37'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_folia_37,  <br>SUM(CASE WHEN  VE_TIPO='4' AND VE_SETOR='88'  AND  VE_DIA='37'  THEN  1  ELSE 0 END) AS qtde_folia_37,  <br>SUM(CASE WHEN  VE_TIPO='6'  AND  VE_DIA='33'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_super_33,  <br>SUM(CASE WHEN  VE_TIPO='6'  AND  VE_DIA='33'  THEN  1  ELSE 0 END) AS qtde_super_33,  <br>SUM(CASE WHEN  VE_TIPO='6'  AND  VE_DIA='34'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_super_34,  <br>SUM(CASE WHEN  VE_TIPO='6'  AND  VE_DIA='34'  THEN  1  ELSE 0 END) AS qtde_super_34,  <br>SUM(CASE WHEN  VE_TIPO='6'  AND  VE_DIA='35'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_super_35,  <br>SUM(CASE WHEN  VE_TIPO='6'  AND  VE_DIA='35'  THEN  1  ELSE 0 END) AS qtde_super_35,  <br>SUM(CASE WHEN  VE_TIPO='6'  AND  VE_DIA='36'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_super_36,  <br>SUM(CASE WHEN  VE_TIPO='6'  AND  VE_DIA='36'  THEN  1  ELSE 0 END) AS qtde_super_36,  <br>SUM(CASE WHEN  VE_TIPO='6'  AND  VE_DIA='37'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_super_37,  <br>SUM(CASE WHEN  VE_TIPO='6'  AND  VE_DIA='37'  THEN  1  ELSE 0 END) AS qtde_super_37,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  AND  VE_DIA='33'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_lounges_33,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  AND  VE_DIA='33'  THEN  1  ELSE 0 END) AS qtde_lounges_33,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  AND  VE_DIA='34'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_lounges_34,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  AND  VE_DIA='34'  THEN  1  ELSE 0 END) AS qtde_lounges_34,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  AND  VE_DIA='35'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_lounges_35,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  AND  VE_DIA='35'  THEN  1  ELSE 0 END) AS qtde_lounges_35,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  AND  VE_DIA='36'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_lounges_36,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  AND  VE_DIA='36'  THEN  1  ELSE 0 END) AS qtde_lounges_36,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  AND  VE_DIA='37'  THEN  (li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) - ((li.LI_VALOR_TABELA - li.LI_DESCONTO + li.LI_OVER_INTERNO) * lo.LO_COMISSAO / 100) - ISNULL(CASE WHEN tx.TX_TAXA IS NOT NULL THEN li.LI_VALOR * (tx.TX_TAXA / 100) ELSE 0 END, 0)  ELSE 0 END) AS valor_lounges_37,  <br>SUM(CASE WHEN  VE_TIPO IN ('4','6')  AND  VE_DIA='37'  THEN  1  ELSE 0 END) AS qtde_lounges_37, 
	MAX(lo.LO_COD) AS LO_COD

	FROM loja_itens li, vendas ve, loja lo
	LEFT JOIN taxa_cartao tx 
		ON (lo.LO_FORMA_PAGAMENTO=1 AND lo.LO_CARTAO=tx.TX_CARTAO AND lo.LO_PARCELAS >= tx.TX_PARCELAS_INICIO AND lo.LO_PARCELAS <= tx.TX_PARCELAS_FIM)
		OR (lo.LO_FORMA_PAGAMENTO=6 AND tx.TX_CARTAO='pos')

	WHERE lo.LO_COD=li.LI_COMPRA
	AND li.D_E_L_E_T_='0'
	AND lo.LO_BLOCK='0'
	AND lo.D_E_L_E_T_='0'
	AND li.LI_INGRESSO=ve.VE_COD
	AND ve.VE_EVENTO='7'
	AND ve.VE_BLOCK='0'
	AND ve.D_E_L_E_T_='0'
	AND lo.LO_EVENTO='7'
	AND lo.LO_FORMA_PAGAMENTO NOT IN (8,9)
	 AND CONVERT(DATE, lo.LO_DATA_COMPRA) = '2018-02-01' 
	 